    public static UserLoginDto setPropertiesFromUserLoginToDto(UserLogin userLogin) {
        UserLoginDto userLoginDto = new UserLoginDto();
        if(userLogin.getUsername() != null) {
            userLoginDto.setUsername(userLogin.getUsername());
            userLoginDto = setLoginPreferenceBasedOnUsername(userLogin.getUsername(), userLoginDto);
        }
        if(userLogin.getPassword() != null)
            userLoginDto.setPassword(userLogin.getPassword());
        else {
            userLoginDto.setPassword(userLogin.getUsername());
        }
        if(UtilValidator.isNotEmpty(userLogin.getPerson())){
            userLoginDto.setFirstName(userLogin.getPerson().getFirstName());
            userLoginDto.setMiddleName(userLogin.getPerson().getMiddleName());
            userLoginDto.setLastName(userLogin.getPerson().getLastName());
            userLoginDto.setVerified(true);
            if(UtilValidator.isNotEmpty(userLogin.getPerson().getContacts())) {
                userLoginDto.setMobileNumber(userLogin.getPerson().getContacts().getMobileNumber());
                userLoginDto.setEmailId(userLogin.getPerson().getContacts().getEmail());
            }
        }
        userLoginDto.setRoles(getRoleOfTheLoggedInUser(userLogin));
        userLoginDto.setDefaultRole(getDefaultRole(userLogin));
        userLoginDto.setProviderType(ProviderType.PHYSIOTHERAPY_CENTER);
        return userLoginDto;
    }

    private static String getDefaultRole(UserLogin userLogin) {
        return Roles.getRoleName(userLogin.getSelectedDefaultRole());
    }

